sourcesContent:
const crypto = require('crypto');
const dbClient = require('../utils/db');
// const redisClient = require('../utils/redis');
const { MongoClient } = require('mongodb');

const UsersController = {
  postNew: async (req, res) => {
    const email = req.body ? req.body.email : null;
    const password = req.body ? req.body.password : null;

    if (!password) {
      res.status(400).json({ error: 'Missing password' });
      return;
    }
    if (!email) {
      res.status(400).json({ error: 'Missing email' });
      return;
    }

    try {
		  // const client = new MongoClient(`mongodb://localhost:27017/files_manager`, { useUnifiedTopology: true });
      // client.connect();
      // const user = client.db().collection('users').findOne();
      const user = await dbClient.client.db().collection('users').findOne({ email });
      if (user) {
        return res.status(400).json({ error: 'Already exist' });
      }
    } catch (error) {
      throw new Error('findOne Error');
    }

    const hashObject = crypto.createHash('sha1');
    hashObject.update(password);
    const hashPass = hashObject.digest('hex');

    try {
      // const client = new MongoClient(`mongodb://localhost:27017/files_manager`, { useUnifiedTopology: true });
      // client.connect();
      // const newUser = client.db().collection('users').insertOne({ email, password: hashPass });
      const newUser = await dbClient.client.db().collection('users').insertOne({ email, password: hashPass });
      return res.status(201).json({ id: newUser.insertedId, email: newUser.ops[0].email });
    } catch (error) {
      throw new Error('insertOne Error');
    }
  },
};
module.exports = UsersController;
